import {FILTER_BY_STATUS,  FILTER_CREATED,  GET_VIDEOGAMES} from "./actions"

const initialState={
    videogames:[],
    allVideogames:[]
};

const rootReducer=(state=initialState, action)=>{
    switch(action.type){
        case GET_VIDEOGAMES:
            return {...state, 
                videogames:action.payload, 
                allVideogames:action.payload }
        case FILTER_BY_STATUS:
            const allVideogames=state.allVideogames
            const statusFiltered=action.payload==='All' ? allVideogames : allVideogames.filter(elem=>elem.status === action.payload);
            return {
                ...state,
            videogames:statusFiltered }
        case FILTER_CREATED:
            const allVideogamesc=state.allVideogames
            const createdFiltered=action.payload==='created' ? state.allVideogamesc.filter(elem=>elem.created) : state.allVideogamesc.filter(elem=>!elem.created);
            return {...state,
            videogames:action.payload==="All" ? state.allVideogames : createdFiltered }
        default:
            return state;
    }
}
export default rootReducer
