import {FILTER_BY_STATUS,  GET_VIDEOGAMES} from "./actions"

const initialState={
    videogames:[],
    allVideogames:[]
};

const rootReducer=(state=initialState, action)=>{
    switch(action.type){
        case GET_VIDEOGAMES:
            return {...state, videogames:action.payload, allVideogames:action.payload };
        case FILTER_BY_STATUS:
            const allVideogames=state.videogames
            const statusFiltered=action.payload==='All' ? allVideogames : allVideogames.filter(elem=>elem.status===action.payload)
            return {...state,
            videogames:statusFiltered }
        default:
            return{...state}
    }
}
export default rootReducer
